const newGameButton = document.getElementById("newGame");

let gameId;
let player;
let board;
let ganador;

export function getGameId(){
    return gameId;
}

export function getBoard(){
    return board;
}

export function getGanador(){
    return ganador;
}

export async function createGame(){
    fetch(`http://localhost:8080/create`)
    .then(response => response.json())
    .then(data => {
        gameId = data.idPartida;
        console.log(gameId)
        player = 'X';
        board = data.board;
        console.log(board);
    })
    .catch(error => {
        console.error("Error:", error);
    });
}

export function turno(casilla) {
    fetch(`http://localhost:8080/turno/${gameId}`, 
        {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({ casilla, player })
        }
    )
    .then(response => response.json())
    .then(data => {
        if (data.success) {
            board = data.board;
            return board;
        } else {
            console.error("Turno no válido:", data.message);
        }
    })
    .catch(error => {
        console.error("Error:", error);
    });
}

export async function reloadBoard() {
    while (!gameId) {
        await new Promise(resolve => setTimeout(resolve, 1000));
    }
    fetch(`http://localhost:8080/actualizar/${gameId}`)
    .then(response => response.json())
    .then(data => {
        if (data.success) {
            board = data.board;
            console.log(board);
            return board;
        }
    }).catch(error => console.error("Error:", error));
}
