let gameId;
let playerId;
let board;
let winner;

export function getGameId(){
    return gameId;
}

export function getBoard(){
    return board;
}

export function getWinner(){
    return winner;
}

export async function createGame(){
    fetch(`http://localhost:8080/create`)
    .then(response => response.json())
    .then(data => {
        gameId = data.idPartida;
        playerId = data.idPlayerX;
        board = data.board;
        winner = data.ganador;

        outputGameId.textContent = `Game ID: ${gameId}`;
        outputPlayerXId.textContent = `Player X ID: ${playerId}`; // Actualizado
        outputBoard.textContent = `Board: ${JSON.stringify(board)}`;
        outputWinner.textContent = `Winner: ${winner}`;
    })
    .catch(error => {
        outputError.textContent = `Error: ${error}`;
    });
}

export async function joinGame(){
    gameId = document.getElementById("gameId").value;
    fetch(`http://localhost:8080/join/${gameId}`)
    .then(response => response.json())
    .then(data => {
        gameId = data.idPartida;
        console.log(gameId)
        playerId = data.idPlayerX;
        board = data.board;
        console.log(board);
        winner = data.ganador;
        console.log(winner);
    })
    .catch(error => {
        console.error("Error:", error);
    });
}

export function turno(casilla) {
    console.log(turno);
    console.log(playerId);
    fetch(`http://localhost:8080/turno/${gameId}`, 
        {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({ casilla: turno, idJugador: playerId })
        })
    .then(response => response.json())
    .then(data => {
        turno++;
        board = data.board;
        winner = data.ganador;

        outputBoard.textContent = `Board: ${JSON.stringify(board)}`;
        outputWinner.textContent = `Winner: ${winner}`;
    })
    .catch(error => {
        outputError.textContent = `Error: ${error}`;
    });
}

export async function reloadBoard() {
    while (!gameId) {
        await new Promise(resolve => setTimeout(resolve, 1000));
    }
    console.log(gameId);
    fetch(`http://localhost:8080/actualizar/${gameId}`)
    .then(response => response.json())
    .then(data => {
        if (data.success) {
            board = data.board;
            console.log(board);
            winner = data.ganador;
            console.log(winner);
        } else {
            console.error("Error al actualizar:", data.message);
        }
    }).catch(error => console.error("Error:", error));
}
