import { useState, useEffect } from "react";
import { getBoard, reloadBoard } from "./Api";

export default function Board() {
  const [xIsNext, setXIsNext] = useState(true);
  const [squares, setSquares] = useState(getBoard() || Array(9).fill(null));
  const winner = calculateWinner(squares);

  function handleClick(i) {
    if (squares[i] || winner) return;

    const nextSquares = squares.slice();
    xIsNext ? (nextSquares[i] = "X") : (nextSquares[i] = "O");

    setSquares(nextSquares);
    setXIsNext(!xIsNext);
  }

  // Función para reiniciar el juego
  function handleNewGame() {
    createGame();
    setSquares(reloadBoard());
    setXIsNext(true);
  }

  // useEffect para manejar el botón externo
  useEffect(() => {
    console.log("useEffect");
    const newGameButton = document.getElementById("newGame");
    if (newGameButton) {
      newGameButton.addEventListener("click", handleNewGame);
    }

    // Cleanup para evitar múltiples listeners
    return () => {
      if (newGameButton) {
        newGameButton.removeEventListener("click", handleNewGame);
      }
    };
  }, []);

  let status = winner ? `Ganador: ${winner}!` : `Siguiente jugador: ${xIsNext ? "X" : "O"}`;

  return (
    <>
      <div className="status">{status}</div>
      <div className="board-row">
        <Square value={squares[0]} onSquareClick={() => handleClick(0)} />
        <Square value={squares[1]} onSquareClick={() => handleClick(1)} />
        <Square value={squares[2]} onSquareClick={() => handleClick(2)} />
      </div>
      <div className="board-row">
        <Square value={squares[3]} onSquareClick={() => handleClick(3)} />
        <Square value={squares[4]} onSquareClick={() => handleClick(4)} />
        <Square value={squares[5]} onSquareClick={() => handleClick(5)} />
      </div>
      <div className="board-row">
        <Square value={squares[6]} onSquareClick={() => handleClick(6)} />
        <Square value={squares[7]} onSquareClick={() => handleClick(7)} />
        <Square value={squares[8]} onSquareClick={() => handleClick(8)} />
      </div>
    </>
  );
}

function Square({ value, onSquareClick }) {
  return (
    <button className="square" onClick={onSquareClick}>
      {value}
    </button>
  );
}

function calculateWinner(squares) {
  if (!Array.isArray(squares) || squares.length !== 9) {
    return null; // Si squares no es un array válido, no hay ganador
  }

  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
